package com.demo;

public class loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		while(n<=10) {
			System.out.print(n+ " ");
		n++;
			
		}
		System.out.println();
		for(int i=1;i<=10;i++) {
			System.out.print(i+ " ");
			
		}
		do {
			System.out.println(n +" ");
		} while(n<=10);
			
	    }	
			
		
		

	}


